deck.on("ready",(()=>{!function(){const e=document.querySelector("#api-example-slide"),t=e.querySelector("button.run-button");let n=!0;deck.on("slidechanged",(t=>{n=t.currentSlide===e})),t.addEventListener("click",(()=>{n&&fetch("https://randomuser.me/api/?nat=us&randomapi").then((e=>e.json())).then((e=>{const t=e.results[0];alert("Got a random user named "+t.name.first+" "+t.name.last)}))}))}()}));